import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
  const [loginId, setLoginId] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/login', { loginId, password });
      console.log('로그인 성공:', response.data);
      navigate('/'); // 로그인 성공하면 홈으로 이동
    } catch (err) {
      setError('아이디 또는 비밀번호가 일치하지 않습니다.');
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="text-center" style={{ maxWidth: "560px" }}>
        <h2 className="mb-4">로그인</h2>
        {error && <div className="alert alert-danger">{error}</div>}
        <form className="w-100" onSubmit={handleLogin}>
          <div className="mb-3">
            <label className="form-label">아이디</label>
            <input type="text" className="form-control" placeholder="아이디"
              value={loginId} onChange={(e) => setLoginId(e.target.value)} />
          </div>
          <div className="mb-3">
            <label className="form-label">비밀번호</label>
            <input type="password" className="form-control" placeholder="비밀번호"
              value={password} onChange={(e) => setPassword(e.target.value)} />
          </div>
          <div className="d-flex gap-2 justify-content-center">
            <button type="submit" className="btn btn-primary btn-lg">로그인</button>
            <Link to="/" className="btn btn-secondary btn-lg">홈으로 이동</Link>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
