  const [member, setMember] = useState(null);
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const memberId = params.get("id"); // üîπ URLÏóêÏÑú ÌöåÏõê ID Í∞ÄÏ†∏Ïò§Í∏∞
  const isNew = params.has("isNew"); // üîπ ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ Ïó¨Î∂Ä ÌôïÏù∏

  useEffect(() => {
    if (memberId) {
      axios.get(`http://localhost:8080/api/members/${memberId}`) // üîπ Ï†ÑÎã¨Îêú IDÎ°ú ÌöåÏõê Ï†ïÎ≥¥ ÏöîÏ≤≠
        .then(response => setMember(response.data))
        .catch(error => console.error('ÌöåÏõê Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', error));
    }
  }, [memberId]);

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="container text-center" style={{ maxWidth: "560px" }}>
        {isNew && <h2 className="mb-3 text-success">ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ!</h2>}

        <h2 className="mb-4">ÌöåÏõê Ï†ïÎ≥¥</h2>

        {member ? (
          <>
            <p className="fw-bold">ÏïÑÏù¥Îîî: <span>{member.loginId}</span></p>
            <p className="fw-bold">Ïù¥Î¶Ñ: <span>{member.name}</span></p>
            <p className="fw-bold">ÌöåÏõê Îì±Í∏â: <span>{member.grade}</span></p>
          </>
        ) : (
          <p className="text-danger">ÌöåÏõê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.</p>
        )}

        <div className="mt-4">
          <Link to="/" className="btn btn-secondary btn-lg">ÌôàÏúºÎ°ú Ïù¥Îèô</Link>
        </div>
      </div>
    </div>
  );
}

export default MemberInfo;
