import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Home() {
  const [member, setMember] = useState(null);

  // 로그인된 회원 정보 가져오기
  useEffect(() => {
    axios.get('http://localhost:8080/api/member') // 로그인한 멤버 정보 API 호출
      .then(response => {
        setMember(response.data);
      })
      .catch(err => {
        setMember(null);
      });
  }, []);

  return (
    <div className="container">
      <h2 className="mb-4 text-center">{member ? '환영합니다!' : '홈 화면'}</h2>

      {!member ? (
        <div className="d-flex gap-3 justify-content-center">
          <Link to="/register" className="btn btn-secondary btn-lg">회원 가입</Link>
          <Link to="/login" className="btn btn-dark btn-lg">로그인</Link>
        </div>
      ) : member.grade === 'ADMIN' ? (
        <div className="d-flex flex-column align-items-center">
          <h3 className="mb-4">관리자 메뉴</h3>
          <div className="d-grid gap-2">
            <Link to="/admin/member-list" className="btn btn-outline-dark btn-lg">회원 목록 조회</Link>
            <Link to="/admin/order-list" className="btn btn-outline-dark btn-lg">주문 목록 조회</Link>
            <Link to="/admin/product-register" className="btn btn-outline-dark btn-lg">상품 등록</Link>
          </div>
        </div>
      ) : (
        <div className="d-flex flex-column align-items-center">
          <h3 className="mb-4">회원 메뉴</h3>
          <div className="d-grid gap-2">
            <Link to="/products" className="btn btn-outline-primary btn-lg">상품 목록</Link>
            <Link to="/order" className="btn btn-outline-primary btn-lg">주문하기</Link>
            <Link to="/orders" className="btn btn-outline-primary btn-lg">내 주문 목록</Link>
            <Link to="/member-info" className="btn btn-outline-primary btn-lg">내 정보 보기</Link>
          </div>
        </div>
      )}
    </div>
  );
}

export default Home;
