import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Register() {
  const [formData, setFormData] = useState({ loginId: '', password: '', name: '' });
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/members/register', formData);
      console.log('회원가입 성공:', response.data);
      navigate(`/members/${response.data.id}?isNew=true`); // 회원 정보 페이지로 이동
    } catch (err) {
      setError(err.response?.data?.message || '회원가입 실패');
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="text-center" style={{ maxWidth: "560px" }}>
        <h2 className="mb-4">회원 가입</h2>
        {error && <div className="alert alert-danger">{error}</div>}
        <form onSubmit={handleRegister} className="w-100">
          <div className="mb-3">
            <label className="form-label">아이디</label>
            <input type="text" className="form-control" name="loginId" placeholder="아이디"
              value={formData.loginId} onChange={handleChange} />
          </div>
          <div className="mb-3">
            <label className="form-label">비밀번호</label>
            <input type="password" className="form-control" name="password" placeholder="비밀번호"
              value={formData.password} onChange={handleChange} />
          </div>
          <div className="mb-3">
            <label className="form-label">이름</label>
            <input type="text" className="form-control" name="name" placeholder="이름"
              value={formData.name} onChange={handleChange} />
          </div>
          <div className="d-flex gap-2 justify-content-center">
            <button type="submit" className="btn btn-primary btn-lg">가입하기</button>
            <Link to="/" className="btn btn-secondary btn-lg">홈으로 이동</Link>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Register;
