import React, { useEffect, useState } from 'react';
import axios from 'axios';

function MemberInfo() {
  const [member, setMember] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8080/api/member') // 로그인한 멤버 정보 API 호출
      .then(response => {
        setMember(response.data);
      })
      .catch(err => {
        setMember(null);
      });
  }, []);

  return (
    <div className="container">
      <h2 className="mb-4 text-center">회원 정보</h2>
      {member ? (
        <div>
          <p>아이디: {member.loginId}</p>
          <p>이름: {member.name}</p>
          <p>회원 등급: {member.grade}</p>
        </div>
      ) : (
        <p>로그인 후에 확인할 수 있습니다.</p>
      )}
    </div>
  );
}

export default MemberInfo;
