import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';

function MemberInfo() {
  const { id } = useParams();
  const [member, setMember] = useState(null);

  useEffect(() => {
    const fetchMember = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/members/${id}`);
        setMember(response.data);
      } catch (error) {
        console.error('회원 정보 조회 실패:', error);
      }
    };
    fetchMember();
  }, [id]);

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="text-center" style={{ maxWidth: "560px" }}>
        <h2 className="mb-4">회원 정보</h2>
        {member ? (
          <>
            <p className="fw-bold">아이디: {member.loginId}</p>
            <p className="fw-bold">이름: {member.name}</p>
            <p className="fw-bold">회원 등급: {member.grade}</p>
          </>
        ) : (
          <p>회원 정보를 불러오는 중...</p>
        )}
        <div className="mt-4">
          <Link to="/" className="btn btn-secondary btn-lg">홈으로 이동</Link>
        </div>
      </div>
    </div>
  );
}

export default MemberInfo;
