import React from 'react';
import { Link } from 'react-router-dom';

function Home() {
  const user = JSON.parse(localStorage.getItem('user'));

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="text-center" style={{ maxWidth: "560px" }}>
        <h2 className="mb-4">{user ? `환영합니다, ${user.name}!` : '홈 화면'}</h2>

        {/* ⬇️ 버튼들을 세로로 정렬하려면 flex-column 추가 */}
        <div className="d-flex flex-column gap-3 align-items-center">
          {!user ? (
            <>
              <Link to="/members/register" className="btn btn-secondary btn-lg">
                회원 가입
              </Link>
              <Link to="/login" className="btn btn-dark btn-lg">
                로그인
              </Link>
            </>
          ) : (
            <>
              {user.grade !== 'ADMIN' ? (
                <>
                  <Link to="/" className="btn btn-outline-primary btn-lg">상품 목록</Link>
                  <Link to="/" className="btn btn-outline-primary btn-lg">주문하기</Link>
                  <Link to="/" className="btn btn-outline-primary btn-lg">내 주문 목록</Link>
                  <Link to={`/member-info?id=${user.id}`} className="btn btn-outline-primary btn-lg">
                    내 정보 보기
                  </Link>
                </>
              ) : (
                <>
                  <Link to="/" className="btn btn-outline-dark btn-lg">회원 목록 조회</Link>
                  <Link to="/" className="btn btn-outline-dark btn-lg">주문 목록 조회</Link>
                  <Link to="/" className="btn btn-outline-dark btn-lg">상품 등록</Link>
                </>
              )}
              <button className="btn btn-danger btn-lg" onClick={() => {
                localStorage.removeItem('user');
                window.location.reload();
              }}>
                로그아웃
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default Home;
