import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function Home() {
  // 로그인 상태 관리 (sessionStorage 또는 localStorage 사용)
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // 로그인 상태를 sessionStorage에서 가져옵니다.
    const loginStatus = sessionStorage.getItem("isLoggedIn");
    if (loginStatus === "true") {
      setIsLoggedIn(true);
    }
  }, []);

  return (
    <div className="container">
      <h2 className="mb-4 text-center">홈 화면</h2>
      <div className="d-flex gap-3 justify-content-center">
        {!isLoggedIn ? (
          <>
            <Link to="/members/register" className="btn btn-secondary btn-lg">
              회원 가입
            </Link>
            <Link to="/login" className="btn btn-dark btn-lg">
              로그인
            </Link>
          </>
        ) : (
          <h3>환영합니다! 로그인이 되었습니다.</h3>
        )}
      </div>
    </div>
  );
}

export default Home;c
