import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function MemberInfo() {
  const [member, setMember] = useState(null);
  const [error, setError] = useState(null);

  const params = new URLSearchParams(window.location.search);
  const memberId = params.get("id"); // URL에서 id 가져오기

  useEffect(() => {
    if (!memberId) {
      setError("회원 ID가 없습니다.");
      return;
    }

    axios.get(`http://localhost:8080/api/members/${memberId}`)
      .then(response => setMember(response.data))
      .catch(error => setError("회원 정보를 불러올 수 없습니다."));
  }, [memberId]);

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="container text-center" style={{ maxWidth: "560px" }}>
        {params.has("isNew") && (
          <h2 className="mb-3 text-success">회원가입 성공!</h2>
        )}

        <h2 className="mb-4">회원 정보</h2>

        {error ? (
          <p className="text-danger">{error}</p>
        ) : member ? (
          <>
            <p className="fw-bold">아이디: <span>{member.loginId}</span></p>
            <p className="fw-bold">이름: <span>{member.name}</span></p>
            <p className="fw-bold">회원 등급: <span>{member.grade}</span></p>
          </>
        ) : (
          <p className="text-danger">회원 정보를 불러올 수 없습니다.</p>
        )}

        <div className="mt-4">
          <Link to="/" className="btn btn-secondary btn-lg">홈으로 이동</Link>
        </div>
      </div>
    </div>
  );
}

export default MemberInfo;
