import React, { useState } from 'react';
import axios from 'axios';

function Register() {
  const [loginId, setLoginId] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [error, setError] = useState('');

  const handleRegister = (e) => {
    e.preventDefault();
    axios.post('http://localhost:8080/api/members/register', { loginId, password, name })
      .then(response => {
        window.location.href = `/members/${response.data.id}`; // 회원 가입 후 회원 정보 페이지로 리디렉션
      })
      .catch(error => {
        setError('가입에 실패했습니다. 다시 시도해주세요.');
      });
  };

  return (
    <div className="container">
      <h2 className="mb-4 text-center">회원 가입</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <form onSubmit={handleRegister}>
        <div className="mb-3">
          <label className="form-label">아이디</label>
          <input
            type="text"
            className="form-control"
            value={loginId}
            onChange={e => setLoginId(e.target.value)}
            placeholder="아이디"
          />
        </div>
        <div className="mb-3">
          <label className="form-label">비밀번호</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={e => setPassword(e.target.value)}
            placeholder="비밀번호"
          />
        </div>
        <div className="mb-3">
          <label className="form-label">이름</label>
          <input
            type="text"
            className="form-control"
            value={name}
            onChange={e => setName(e.target.value)}
            placeholder="이름"
          />
        </div>
        <button type="submit" className="btn btn-primary btn-lg">가입하기</button>
      </form>
    </div>
  );
}

export default Register;
